import React from "react";

const Main = ({ children }) => {
  return (
    <main
      id="main"
      role="main"
      style={{
        display: "flex",
        flexDirection: "column",
        height: "100vh",
        overflowY: "scroll", // Enable vertical scrolling if needed
      }}
    >
      {React.Children.map(children, (child, index) => (
        <div
          key={index}
          style={{
            flex: "1",
            display: "flex",
            flexDirection: "column",
          }}
        >
          {child}
        </div>
      ))}
    </main>
  );
};

export default Main;
