import React from "react";
import { BrowserRouter, Route, Routes } from "react-router-dom";
import Particles from "react-particles"; // Correct import
import { loadFull } from "tsparticles";
import { useCallback } from "react";

import Wrapper from './components/Wrapper/Wrapper';

const App = () => {
  const options = {
    background: {
      color: "#F0F2F4", // Set your background color here
    },
    particles: {
      shape: {
        type: "circle"
      },
      number: {
        value: 100 // Set the number of particles
      },
      color: {
        value: "#000000" // Set particle color
      },
      opacity: {
        value: 0.3
      },
      size: {
        value: { min: 5, max: 20 }
      },
      move: {
        enable: true,
        speed: 6,
        random: false,
        out_mode: "bounce", // Correct property name
      }
    },
    absorbers: [
      {
        position: { x: 50, y: 50 },
        size: {
          value: 50,
          limit: 100,
        }
      }
    ],
    emitters: [
      {
        direction: "top-right",
        position: {
          x: 0,
          y: 100
        },
        rate: {
          delay: 0.3,
          quantity: 4,
        },
      },
    ]
  };

  const particlesInit = useCallback(async (engine) => {
    await loadFull(engine);
  }, []);

  return (
    <div className="app">
      <Particles params={options} init={particlesInit} /> {/* Use 'params' instead of 'options' */}
      <BrowserRouter>
        <Routes>
          <Route path="/" element={<Wrapper />} />
        </Routes>
      </BrowserRouter>
    </div>
  );
};

export default App;
