import React from "react";
import { Container, Typography } from "@mui/material";

const Intro = () => {
  const containerStyle = {
    backgroundImageavif: `url(${process.env.PUBLIC_URL}/developer.jpeg?as=avif)`,
    backgroundImage: `url(${process.env.PUBLIC_URL}/developer.jpeg)`,
    backgroundImagewebp: `url(${process.env.PUBLIC_URL}/developer.jpeg?as=webp)`,
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat",
    minHeight: "100vh", // Set the height of the container to full viewport height
    minWidth: "100%",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    color: "white", // Set the text color to contrast with the background
  };

  return (
    <Container id="intro" style={containerStyle}>
      <Typography variant="h2" align="center" mt={5} mb={3}>
        Welcome to My Portfolio
      </Typography>
      <Typography variant="body1" align="center" color="textSecondary" paragraph>
        Hi, I'm a passionate front-end developer. Explore my projects and skills to learn more about my work.
      </Typography>
    </Container>
  );
};

export default Intro;
