import React from "react";
import { Box, Container, Typography } from "@mui/material";

const Footer = () => {

    const domTarget = useRef(null);
    const [springProps, set] = useSpring(() => ({ opacity: 0 }));

    useEffect(() => {
        const handleScroll = () => {
            const options = {
                root: null,
                rootMargin: '0px',
                threshold: 0.4,
            };

            const observer = new IntersectionObserver((entries) => {
                entries.forEach((entry) => {
                    if (entry.isIntersecting) {
                        const newOpacity = Math.max(1, Math.max(0, (window.innerHeight - entry.boundingClientRect.top) / (entry.boundingClientRect.height)));
                        set({ opacity: newOpacity });
                    } else {
                        set({ opacity: 0 });
                    }
                });
            }, options);

            if (domTarget.current) {
                observer.observe(domTarget.current);
            }
        };

        window.addEventListener("scroll", handleScroll);
        handleScroll();

        return () => {
            window.removeEventListener("scroll", handleScroll);
        };
    }, [set, domTarget]);

    const footerStyle = {
        position: 'fixed',
        bottom: 0,
        width: '100%',
    };

    return (
        <animated.div
                ref={domTarget}
                style={{
                    opacity: springProps.opacity,
                    transition: "opacity 0.5s ease",
                }}
            >
        <Box sx={footerStyle}>
            <Container component="footer" mt={5} py={3} bgcolor="primary.main" color="white">
                <Typography variant="body2" align="center">
                    &copy; 2024 Heewon Kim. All rights reserved.
                </Typography>
            </Container>
        </Box>
        </animated.div>
    );
};

export default Footer;
