import React, { useState } from 'react';

const SkillPaper = ({ title, level }) => {
  const sheetStyle = {
    position: 'absolute',
    width: '100%',
    height: '100%',
    backgroundColor: '#eee',
    boxShadow: '2px 1px 1px rgba(0,0,0,0.15)',
    margin: '10px',
  };

  const levelStyle = {
    height: '100%',
    width: `${level}%`,
    backgroundColor: '#3498db',
  };

  return (
    <div style={sheetStyle}>
      <div style={levelStyle}></div>
      <div style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', zIndex: '1' }}>
        {title}
      </div>
    </div>
  );
};

const Paper = () => {
  const [isHovered, setIsHovered] = useState(false);

  const handleMouseEnter = () => {
    setIsHovered(true);
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
  };

  const skills = [
    ...frontendSkills,
    ...backendSkills,
    ...commonSkills,
  ];

  return (
    <div
      style={{
        position: 'relative',
        backgroundColor: '#fff',
        padding: '30px',
        transition: 'transform 0.3s ease-in-out',
        transform: isHovered ? 'translateX(20px)' : 'translateX(0)',
      }}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      {skills.map((skill, index) => (
        <SkillPaper key={index} title={skill.title} level={skill.level} />
      ))}
    </div>
  );
};

const frontendSkills = [
  { title: "JavaScript", level: 90 },
  { title: "React.js", level: 85 },
  { title: "HTML5", level: 90 },
  { title: "CSS3", level: 85 },
  { title: "HTML5", level: 95 },
  { title: "CSS", level: 100 },
  { title: "Figma", level: 90 },
];

const backendSkills = [
  { title: "Java", level: 90 },
  { title: "C", level: 75 },
  { title: "Python", level: 65 },
  { title: "TypeScript", level: 70 },
  { title: "R", level: 80 },
  { title: "MySQL", level: 85 },
  { title: "React-Native", level: 85 },
  { title: "FastAPI", level: 60 },
  { title: "JSON", level: 90 },
  { title: "Node.js", level: 90 },
];

const commonSkills = [
  { title: "Postman", level: 80 },
  { title: "Git", level: 95 },
  { title: "Linux", level: 70 },
];

export default Paper;
