import React from "react";
import { Container, Typography, Grid, Card, CardContent } from "@mui/material";

// Your skills data
const programmingSkills = [
  { title: "JavaScript" },
  { title: "React.js" },
  { title: "Java" },
  { title: "C" },
  { title: "Python" },
  { title: "TypeScript" },
  { title: "R" },
];

const markupAndStylesheetSkills = [
  { title: "HTML5" },
  { title: "CSS3" },
  { title: "HTML5" },
  { title: "CSS" },
];

const databaseAndDataFormatSkills = [
  { title: "MySQL" },
  { title: "JSON" },
];

const frameworkSkills = [
  { title: "React-Native" },
  { title: "FastAPI" },
];

const designToolSkills = [
  { title: "Figma" },
];

const toolsAndPracticesSkills = [
  { title: "Postman" },
  { title: "Git" },
  { title: "Github" },
  { title: "Linux" },
];

const Skill = () => {
  const skillsData = [
    { category: "Programming Skills", skills: programmingSkills },
    { category: "Markup and Stylesheet Languages", skills: markupAndStylesheetSkills },
    { category: "Database and Data Format", skills: databaseAndDataFormatSkills },
    { category: "Frameworks", skills: frameworkSkills },
    { category: "Design Tool", skills: designToolSkills },
    { category: "Tools and Practices", skills: toolsAndPracticesSkills },
  ];

  return (
    <Container
      id="intro"
      sx={{
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        height: "100%",
      }}
    >
      <Typography variant="h2" fontWeight={"bold"} align="center" mb={10}>
        Skills
      </Typography>

      {skillsData.map((category, index) => (
        <div key={index}>
          <Typography variant="h4" mb={2}>
            {category.category}
          </Typography>
          <Grid container spacing={3}>
            {category.skills.map((skill, skillIndex) => (
              <Grid item key={skillIndex} xs={12} sm={6} md={4} lg={3}>
                <Card>
                  <CardContent>
                    <Typography variant="h6">{skill.title}</Typography>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        </div>
      ))}
    </Container>
  );
};

export default Skill;
