// Scroll.jsx

import React, { useState, useEffect, useRef } from "react";

import Header from "../Header/Header";
import Motion from "./Motion";

const Scroll = ({ components }) => {
    const [activeIndex, setActiveIndex] = useState(0);
    const [scrollPosition, setScrollPosition] = useState(0);
    const [componentHeights, setComponentHeights] = useState([]);

    const scrollRef = useRef();

    useEffect(() => {
        const updateComponentHeights = () => {
            if (scrollRef.current) {
                const heights = components.map((_, index) => {
                    const component = scrollRef.current.children[index + 2]; // Header와 Motion 컴포넌트를 제외하고 계산
                    return component ? component.clientHeight : 0;
                });
                setComponentHeights(heights);
            }
        };

        if (scrollRef.current) {
            window.addEventListener("resize", updateComponentHeights);

            return () => {
                window.removeEventListener("resize", updateComponentHeights);
            };
        }
    }, [components]);

    const handleScroll = () => {
        if (scrollRef.current) {
            const scrollTop = scrollRef.current.scrollTop;
            setScrollPosition(scrollTop);

            const heights = componentHeights.map((height, index) => index * height);
            const newIndex = heights.findIndex((height) => height > scrollTop);
            setActiveIndex(newIndex === -1 ? componentHeights.length - 1 : newIndex);
        }
    };

    const calculateOpacity = (index) => {
        if (scrollRef.current) {
            const componentHeight = componentHeights[index] || 0;
            const distanceToComponent = Math.abs(scrollPosition - index * componentHeight);
            const maxDistance = componentHeight * 0.5;
            const opacity = 1 - Math.min(distanceToComponent / maxDistance, 1);
            return opacity;
        }
        return 1;
    };

    const scrollToComponent = (index) => {
        if (scrollRef.current) {
            const componentHeight = componentHeights[index] || 0;
            scrollRef.current.scrollTo({
                top: index * componentHeight,
                behavior: "smooth",
            });
        }
    };

    const updateSlideState = (index) => {
        scrollToComponent(index);
    };

    return (
        <div
            ref={scrollRef}
            onScroll={handleScroll}
            style={{
                height: "100vh",
                overflowY: "scroll",
                scrollBehavior: "smooth",
            }}
        >
            <Header
                orientation={'vertical'}
                length={components.length}
                currIdx={activeIndex}
                clickHandler={updateSlideState}
            />
            {scrollPosition !== 0 && (
                <Motion
                    scrollPosition={scrollPosition}
                    scrollHeight={scrollRef.current?.scrollHeight}
                    clientHeight={scrollRef.current?.clientHeight}
                />
            )}

            {components.map((component, index) => (
                <div
                    key={index}
                    style={{
                        height: componentHeights[index] || "100vh",
                        opacity: calculateOpacity(index),
                        transition: "opacity 0.2s ease-in-out",
                    }}
                >
                    {React.cloneElement(component, { isActive: index === activeIndex, scrollPosition })}
                </div>
            ))}
        </div>
    );
};

export default Scroll;
