import React, { useState, useEffect, useRef } from "react";
import { motion, useTransform } from "framer-motion";
import {
  Container,
  Typography,
  Box,
  useTheme,
  Button,
} from "@mui/material";

const Single = React.forwardRef(({ project }, ref) => {
  const imageUrl = project.imageUrls[0];
  const [scrollProgress, setScrollProgress] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      const scrollY = window.scrollY;
      const scrollHeight = document.documentElement.scrollHeight - window.innerHeight;
      const progress = scrollY / scrollHeight;
      setScrollProgress(progress);
    };

    // Attach scroll event listener
    window.addEventListener("scroll", handleScroll);

    // Cleanup the event listener on component unmount
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  const y = useTransform(scrollProgress, [0, 1], [-300, 300]);

  return (
    <Container>
      <Box style={{ width: "100%" }}>
        <Box ref={ref}>
          <img
            alt={project.title}
            src={imageUrl}
            style={{
              minHeight: "0",
              minWidth: "0",
              maxHeight: "50vh",
              maxWidth: "80%",
              border: "1px solid #65f9af",
              borderRadius: "10px",
            }}
          />
        </Box>

        <motion.div style={{ y }}>
          <Typography
            color={"#FFF"}
            fontWeight={"bold"}
            textAlign={"center"}
            mb={2}
            sx={{ fontSize: { xs: "8px", sm: "14px", md: "20px" } }}
          >
            {project.title}
          </Typography>
          {/* ... Other content ... */}
        </motion.div>
      </Box>
    </Container>
  );
});

export default Single;
