import React, { useRef, useState, useEffect } from "react";
import { useSpring, animated } from "@react-spring/web";
import { Card, CardContent, Typography, useTheme, useMediaQuery, responsiveFontSizes, ThemeProvider } from "@mui/material";

const SkillCard = ({ skill }) => {
  const theme = useTheme();
  const isSmallScreen = useMediaQuery(theme.breakpoints.down("sm"));
  const responsiveTheme = responsiveFontSizes(theme);
  const domTarget = useRef(null);
  const [opacity, setOpacity] = useState(0);

  const handleScroll = () => {
    if (domTarget.current) {
      const { top, bottom } = domTarget.current.getBoundingClientRect();
      const windowTop = window.scrollY;
      const windowBottom = windowTop + window.innerHeight;

      const fadeInThreshold = 200; // Adjust as needed
      const fadeOutThreshold = 300; // Adjust as needed

      console.log('Top:', top, 'Bottom:', bottom);

      if (bottom > windowTop + fadeInThreshold && top < windowBottom - fadeOutThreshold) {
        console.log('Handling scroll...');
        const newOpacity = Math.min(1, Math.max(0, (window.innerHeight - top) / (bottom - top)));
        setOpacity(newOpacity);
      } else {
        console.log('Element not in visible range.');
        console.log('Condition:', bottom > windowTop + fadeInThreshold, top < windowBottom - fadeOutThreshold);
      }
    }
  };


  useEffect(() => {
    // Attach scroll event listener
    window.addEventListener("scroll", handleScroll);

    // Initial calculation
    handleScroll();

    // Cleanup on component unmount
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <ThemeProvider theme={responsiveTheme}>
      <animated.div
        ref={domTarget}
        style={{
          opacity: opacity,
          transition: "opacity 0.3s ease",
        }}
      >
        <Card variant="outlined" sx={{ width: "90%", height: "100%" }}>
          <CardContent>
            <Typography
              fontWeight={"bold"}
              sx={{
                marginLeft: { xs: "-15px", sm: "-15px", md: "px" },
                fontSize: { xs: "8px", sm: "8px", md: "16px" },
              }}
            >
              {skill}
            </Typography>
          </CardContent>
        </Card>
      </animated.div>
    </ThemeProvider>
  );
};

export default SkillCard;
