import React, { useState } from "react";
import { useSpring, animated } from "react-spring";
import { Container, Typography, Grid, Card, CardContent } from "@mui/material";

const skillsData = [
  // ... (이전 코드 유지)
];

const Skill = () => {
  const [hoveredIndex, setHoveredIndex] = useState(null);

  const handleMouseEnter = (index) => {
    setHoveredIndex(index);
  };

  const handleMouseLeave = () => {
    setHoveredIndex(null);
  };

  return (
    <Container
      id="intro"
      sx={{
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        height: "100%",
      }}
    >
      <Typography variant="h4" fontWeight={"bold"} align="center" mb={5}>
        Skills
      </Typography>

      <Grid container spacing={1}>
        {skillsData.map((category, index) => (
          <Grid
            item
            key={index}
            xs={12}
            sm={6}
            md={4}
            onMouseEnter={() => handleMouseEnter(index)}
            onMouseLeave={handleMouseLeave}
          >
            <Typography variant="h6" color={"GrayText"} mb={2} textAlign={"center"}>
              {category.category}
            </Typography>
            <Grid container spacing={1}>
              {category.skills.map((skill, skillIndex) => (
                <Grid item key={skillIndex} xs={5} sm={4} md={6}>
                  <animated.div
                    style={{
                      opacity: hoveredIndex === index ? 1 : 0.8,
                      transform: hoveredIndex === index ? "translateY(0px)" : "translateY(-50px)",
                    }}
                  >
                    <Card variant="outlined">
                      <CardContent>
                        <Typography variant="body1" fontWeight={"bold"} textAlign={"center"}>
                          {skill}
                        </Typography>
                      </CardContent>
                    </Card>
                  </animated.div>
                </Grid>
              ))}
            </Grid>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

export default Skill;
