import React from "react";
import { Link, animateScroll as scroll } from "react-scroll";

const Main = ({ children }) => {
    const scrollToTop = () => {
        scroll.scrollToTop();
    };

    return (
        <main id="main" role="main">
            {children}
            <Link
                to="main"
                spy={true}
                smooth={true}
                offset={-70}
                duration={500}
                className="scroll-to-top"
                onClick={scrollToTop}
            >
                Scroll to Top
            </Link>
        </main>
    );
};

export default Main;
