import React, { useEffect, useState } from "react";
import SkillCard from "./SkillCard";
import {
  Container,
  Typography,
  Grid,
  useTheme,
  useMediaQuery,
  responsiveFontSizes,
  ThemeProvider,
} from "@mui/material";

const skillsData = [
  { category: "Language", skills: ["JavaScript", "Java", "C", "Python", "TypeScript", "R"] },
  { category: "Markup", skills: ["HTML5", "CSS3"] },
  { category: "Data", skills: ["MySQL", "JSON"] },
  { category: "Framework", skills: ["React", "React-Native", "FastAPI", "Node.js"] },
  { category: "Tool", skills: ["Postman", "Git", "Github", "Linux", "Jira"] },
  { category: "Design", skills: ["Figma"] },
];

const Skill = () => {
  const theme = useTheme();
  const isSmallScreen = useMediaQuery(theme.breakpoints.down("sm"));
  const responsiveTheme = responsiveFontSizes(theme);

  const [scrollOpacity, setScrollOpacity] = useState(1);

  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY;
      const opacity = 1 - scrollPosition / 500; // Adjust the divisor for the desired fade effect speed
      setScrollOpacity(opacity > 0 ? opacity : 0);
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <ThemeProvider theme={responsiveTheme}>
      <Container
        id="intro"
        sx={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          height: "100%",
          opacity: scrollOpacity,
          transition: "opacity 0.5s ease-in-out", // Adjust the duration and timing function for the desired fade effect
        }}
      >
        <Typography fontWeight={"bold"} variant="h2" mb={3} style={{ fontSize: isSmallScreen ? "1em" : "3em", textAlign: "center" }}>
          Skills
        </Typography>

        <Grid container spacing={1}>
          {skillsData.map((category, index) => (
            <Grid item key={index} xs={4} sm={6} md={4}>
              <Typography color={"GrayText"} mb={2} textAlign={"center"} sx={{ fontSize: { xs: '12px', sm: '14px', md: '20px' } }}>
                {category.category}
              </Typography>
              <Grid container spacing={1}>
                {category.skills.map((skill, skillIndex) => (
                  <Grid item key={skillIndex} xs={6} sm={4} md={6}>
                    <SkillCard skill={skill} />
                  </Grid>
                ))}
              </Grid>
            </Grid>
          ))}
        </Grid>
      </Container>
    </ThemeProvider>
  );
};

export default Skill;
