import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";
import { wrap } from "@motionone/utils";
import { Typography, useTheme, useMediaQuery } from "@mui/material";

function ParallaxText({ children, baseVelocity = 100, scrollPosition }) {
    const [baseX, setBaseX] = useState(0);

    const x = `${wrap(-50, 50, (scrollPosition / 100) * baseVelocity)}%`;

    useEffect(() => {
        const updateX = () => {
            setBaseX((scrollPosition) * baseVelocity);
        };

        updateX();

        // Add scroll event listener
        window.addEventListener("scroll", updateX);

        return () => {
            // Remove scroll event listener on component unmount
            window.removeEventListener("scroll", updateX);
        };
    }, [scrollPosition, baseVelocity]);

    const theme = useTheme();
    const isSmallScreen = useMediaQuery(theme.breakpoints.down("sm"));

    return (
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
            <div style={{ transform: `translateX(${x})`, width: '100%' }}>
                <Typography color={'#FFF'} fontWeight={"bold"} variant="h1" style={{ fontSize: isSmallScreen ? "3em" : "5.5rem", textAlign: "center" }}>
                    {children}
                </Typography>
            </div>
        </div>
    );
}

export default function ProSkill({ scrollPosition }) {
    return (
        <section style={{ paddingTop: '10rem' }}>
            <ParallaxText scrollPosition={scrollPosition} baseVelocity={-10}>LET'S</ParallaxText>
            <ParallaxText scrollPosition={scrollPosition} baseVelocity={10}>START</ParallaxText>
        </section>
    );
}
