import React from "react";
import { Link } from "react-scroll";

const Main = ({ children }) => {
  return (
    <main
      id="main"
      role="main"
      style={{
        display: "flex",
        flexDirection: "column",
        height: "100vh",
        overflowY: "scroll", // Enable vertical scrolling if needed
      }}
    >
      {React.Children.map(children, (child, index) => (
        <Link key={index} to={`child${index}`} smooth duration={500}>
          {React.cloneElement(child, { id: `child${index}` })}
        </Link>
      ))}
    </main>
  );
};

export default Main;
