import React from "react";
import { Element, animateScroll as scroll } from "react-scroll";

const Main = ({ children }) => {
  const [currentSection, setCurrentSection] = React.useState(0);

  const handleSetActive = (to) => {
    // to 매개변수에 현재 활성화된 섹션의 이름이 전달됩니다.
    // 이를 사용하여 현재 섹션을 업데이트할 수 있습니다.
    setCurrentSection(parseInt(to.replace("section", ""), 10));
  };

  return (
    <main
      id="main"
      role="main"
      style={{
        display: "flex",
        flexDirection: "column",
        height: "100vh",
        overflowY: "scroll", // Enable vertical scrolling if needed
      }}
    >
      {React.Children.map(children, (child, index) => (
        <Element
          key={index}
          name={`section${index}`}
          className="element"
          onSetActive={handleSetActive}
        >
          <div
            style={{
              flex: "1",
              minHeight: "100vh",
              display: "flex",
              flexDirection: "column",
            }}
          >
            {React.cloneElement(child, { isActive: currentSection === index })}
          </div>
        </Element>
      ))}
    </main>
  );
};

export default Main;
