import React, { useState } from 'react';
import { Card, CardContent, Typography } from '@mui/material';

const SkillCard = ({ skill }) => {
    const [isHovered, setIsHovered] = useState(false);
    const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

    const handleMouseEnter = () => {
        setIsHovered(true);
    };

    const handleMouseLeave = () => {
        setIsHovered(false);
    };

    const handleMouseMove = (e) => {
        setMousePosition({ x: e.clientX, y: e.clientY });
    };

    return (
        <>
            <div
                style={{ display: 'flex', justifyContent: 'center', position: 'relative' }}
                onMouseEnter={handleMouseEnter}
                onMouseLeave={handleMouseLeave}
                onMouseMove={handleMouseMove}
            >
                <img
                    alt={skill.name}
                    style={{ maxWidth: "100%", height: "10vh", objectFit: "cover" }}
                    src={skill.image}
                />
                {isHovered && (
                    <div
                        style={{
                            position: 'absolute',
                            top: mousePosition.y - 10, // Adjust the position as needed
                            left: mousePosition.x - 10, // Adjust the position as needed
                            width: 20,
                            height: 20,
                            borderRadius: '50%',
                            backgroundColor: 'rgba(255, 0, 0, 0.5)', // Adjust the color as needed
                        }}
                    ></div>
                )}
            </div>
        </>
    );
};

export default SkillCard;
