import React, { useState, useEffect, useRef } from "react";
import Header from "../Header/Header";

const Scroll = ({ components }) => {
    const [activeIndex, setActiveIndex] = useState(0);
    const [scrollPosition, setScrollPosition] = useState(0);

    const scrollRef = useRef();

    const handleScroll = () => {
        const scrollTop = scrollRef.current.scrollTop;
        const componentHeight = scrollRef.current.clientHeight;

        const newIndex = Math.floor(scrollTop / componentHeight);
        setActiveIndex(newIndex);

        // 스크롤 위치 업데이트
        setScrollPosition(scrollTop);
    };

    useEffect(() => {
        const handleResize = () => {
            const componentHeight = scrollRef.current.clientHeight;
            const newIndex = Math.floor(scrollRef.current.scrollTop / componentHeight);
            setActiveIndex(newIndex);
        };

        window.addEventListener("resize", handleResize);

        return () => {
            window.removeEventListener("resize", handleResize);
        };
    }, []);

    const isInView = (index) => {
        const componentHeight = scrollRef.current.clientHeight;
        const topOffset = index * componentHeight;
        const bottomOffset = (index + 1) * componentHeight;
        return scrollPosition >= topOffset && scrollPosition < bottomOffset;
    };

    const updateSlideState = () => {
        components.forEach((_, index) => {
            if (isInView(index)) {
                setActiveIndex(index);
            }
        });
    };

    const calculateOpacity = (index) => {
        const distanceToComponent = Math.abs(scrollPosition - index * scrollRef.current.clientHeight);
        const maxDistance = 200; // Adjust as needed
        const opacity = 1 - distanceToComponent / maxDistance;
        return opacity < 0 ? 0 : opacity; // Ensure opacity doesn't go below 0
    };

    return (
        <div
            ref={scrollRef}
            onScroll={handleScroll}
            style={{
                height: "100vh",
                overflowY: "scroll",
                scrollBehavior: "smooth",
            }}
        >
            <Header
                orientation={'vertical'}
                length={components.length}
                currIdx={activeIndex}
                clickHandler={updateSlideState}
            />
            {components.map((component, index) => (
                <div
                    key={index}
                    style={{
                        height: "100vh",
                        opacity: calculateOpacity(index),
                        transition: "opacity 0.5s ease-in-out",
                    }}
                >
                    {React.cloneElement(component, { isActive: index === activeIndex, scrollPosition })}
                </div>
            ))}
        </div>
    );
};

export default Scroll;
