import React from "react";
import { Element, scroller } from "react-scroll";

const Main = ({ children }) => {
  const scrollToNextSection = (index) => {
    scroller.scrollTo(`section${index}`, {
      duration: 800,
      delay: 0,
      smooth: "easeInOutQuart",
    });
  };

  return (
    <main>
      {React.Children.map(children, (child, index) => (
        <Element key={index} name={`section${index}`} className="element">
          <div
            style={{
              flex: "1",
              minHeight: "100vh",
              display: "flex",
              flexDirection: "column",
            }}
          >
            {child}
            <button onClick={() => scrollToNextSection(index + 1)}>
              Scroll to Next Section
            </button>
          </div>
        </Element>
      ))}
    </main>
  );
};

export default Main;
