import React, { useState } from "react";
import { SwipeableDrawer } from "@mui/material";
import { Element } from "react-scroll";

const Main = ({ children }) => {
  const [open, setOpen] = useState(false);

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <main>
      {React.Children.map(children, (child, index) => (
        <Element key={index} name={`section${index}`} className="element">
          <div
            style={{
              flex: "1",
              minHeight: "100vh",
              display: "flex",
              flexDirection: "column",
            }}
          >
            {React.cloneElement(child, { onClick: handleOpen })}
          </div>
        </Element>
      ))}
      <SwipeableDrawer
        anchor="right"
        open={open}
        onClose={handleClose}
        onOpen={handleOpen}
      >
        {React.Children.map(children, (child, index) => (
          <div
            style={{
              width: "100%",
              height: "100vh",
              overflow: "hidden",
            }}
          >
            {React.cloneElement(child, { onClick: handleClose })}
          </div>
        ))}
      </SwipeableDrawer>
    </main>
  );
};

export default Main;
