import React, { useState } from 'react';
import { Card, CardContent, Typography } from '@mui/material';
import { borderRadius, fontWeight } from '@mui/system';

const SkillCard = ({ skill }) => {
    const [pentagonPosition, setPentagonPosition] = useState({ x: 0, y: 0 });

    const handleMouseMove = (e) => {
        const boundingBox = e.currentTarget.getBoundingClientRect();
        const mouseX = e.clientX - boundingBox.left - boundingBox.width / 2;
        const mouseY = e.clientY - boundingBox.top - boundingBox.height / 2;

        setPentagonPosition({ x: mouseX, y: mouseY });
    };

    const handleMouseLeave = () => {
        setPentagonPosition({ x: 0, y: 0 });
    };

    return (
        <>
            <div
                style={{
                    display: 'flex',
                    justifyContent: 'center',
                    position: 'relative',
                }}
                onMouseMove={handleMouseMove}
                onMouseLeave={handleMouseLeave}
            >
                <img
                    alt={skill.name}
                    style={{ maxWidth: '100%', height: '10vh', objectFit: 'cover' }}
                    src={skill.image}
                />
                {pentagonPosition.x !== 0 && (
                    <div
                        style={{
                            position: 'absolute',
                            left: pentagonPosition.x,
                            top: pentagonPosition.y,
                            transform: `translate(-50%, -50%)`,
                            width: 0,
                            height: 0,
                            borderLeft: '5rem solid transparent',
                            borderRight: '5rem solid transparent',
                            borderBottom: '10rem solid #FFF',
                            borderTop: '5rem solid transparent',
                        }}
                    >
                        <div
                            style={{
                                marginTop: '45%',
                                color: '#000',
                                textAlign: 'center',
                                fontWeight: 'bold',
                            }}
                        >
                            {skill.name}
                        </div>
                    </div>
                )}
            </div>
        </>
    );
};

export default SkillCard;
