import React from "react";
import {
    Timeline,
    Container,
    Typography,
    Box,
    Button,
    useMediaQuery,
    useTheme,
    responsiveFontSizes,
    ThemeProvider,
    Grid
} from "@mui/material";
import SchoolIcon from '@mui/icons-material/School';
import DevicesIcon from '@mui/icons-material/Devices';

import TimeLineCard from "./TimeLineCard";

const timeLineData = [
    { id: 1, time: "Sept 2019 - Dec 2020", activity: "Madison Area Technical College", details: "Associate's", icon: SchoolIcon },
    { id: 2, time: "Jan 2021 - Dec 2023", activity: "University of Wisconsin - Madison", details: "Bachelor's in Computer Science", icon: SchoolIcon },
    { id: 3, time: "Oct 2022 - Present", activity: "College Mate App, Madison, WI", details: "Co-Founder & Front-end Developer", icon: DevicesIcon },
    { id: 4, time: "Sept 2023 - Dec 2023", activity: "Wisconsin State Cartographer's Office, Madison, WI", details: "Back-end Developer", icon: DevicesIcon }
];

const Resume = () => {
    const theme = responsiveFontSizes(useTheme());
    const isSmallScreen = useMediaQuery(theme.breakpoints.down("sm"));

    const handleDownload = () => {
        window.open("./resume.pdf", "_blank");
    };

    return (
        <ThemeProvider theme={theme}>
            <Container>
                <Typography fontWeight="bold" variant="h2" mb={3} style={{ fontSize: isSmallScreen ? "1.5em" : "3em", textAlign: "center" }}>
                    RESUME
                </Typography>

                <Grid container spacing={4}>
                    <Grid item xs={12} md={4}>
                        <img src="./developer.png" alt="Your Alt Text" style={{ width: "100%"}} />
                    </Grid>

                    <Grid item xs={12} md={8}>
                        <Timeline position={isSmallScreen ? 'alternate' : ''}>
                            {timeLineData.map((timeline) => (
                                <TimeLineCard key={timeline.id} {...timeline} />
                            ))}
                        </Timeline>

                        <Typography variant="subtitle1" mt={2} style={{ fontSize: isSmallScreen ? "0.75em" : "1.2em" }}>
                            Want more information?
                        </Typography>

                        <Button variant="outlined" onClick={handleDownload} style={{ marginBottom: isSmallScreen ? "10px" : "20px" }}>
                            Download Resume
                        </Button>
                    </Grid>
                </Grid>
            </Container>
        </ThemeProvider>
    );
};

export default Resume;
