import React from "react";
import { Container, Typography, Paper } from "@mui/material";

const skillsData = [
    { title: "JavaScript", level: 90 },
    { title: "React.js", level: 85 },
    { title: "HTML5", level: 90 },
    { title: "CSS3", level: 85 },
    { title: "Java", level: 90 },
    { title: "C", level: 75 },
    { title: "Python", level: 65 },
    { title: "TypeScript", level: 70 },
    { title: "HTML5", level: 95 },
    { title: "CSS", level: 100 },
    { title: "R", level: 80 },
    { title: "MySQL", level: 85 },
    { title: "React-Native", level: 85 },
    { title: "Postman", level: 80 },
    { title: "Git", level: 95 },
    { title: "Linux", level: 70 },
    { title: "Figma", level: 90 },
    { title: "FastAPI", level: 60 },
    { title: "JSON", level: 90 },
    { title: "pgAdmin", level: 80 },
    { title: "Jira", level: 90 },
    { title: "Agile", level: 90 },
    { title: "Node.js", level: 90 },
  ];


  const RadarChart = ({ data, labels }) => {
    const maxLevel = 100;

    return (
      <Paper elevation={3} style={{ padding: 20, textAlign: "center" }}>
        <Typography variant="h6" color="textSecondary" gutterBottom>
          Skill Levels
        </Typography>
        <svg height="300" width="300">
          <polygon
            points={data.map((d, i) => {
              const angle = (i * (360 / data.length) * Math.PI) / 180;
              const x = 150 + Math.cos(angle) * (d / maxLevel) * 100;
              const y = 150 + Math.sin(angle) * (d / maxLevel) * 100;
              return `${x},${y}`;
            })}
            fill="rgba(75,192,192,0.4)"
            stroke="rgba(75,192,192,1)"
            strokeWidth="2"
          />
          {labels.map((label, i) => (
            <text
              key={i}
              x={150 + Math.cos((i * (360 / data.length) * Math.PI) / 180) * 120}
              y={150 + Math.sin((i * (360 / data.length) * Math.PI) / 180) * 120}
              fill="black"
              fontSize="12"
              textAnchor="middle"
            >
              {label}
            </text>
          ))}
        </svg>
      </Paper>
    );
  };

  const Skill = () => {
    const skillLevels = skillsData.map((skill) => skill.level);
    const skillLabels = skillsData.map((skill) => skill.title);

    return (
      <div style={{ overflowY: "auto", maxHeight: "calc(100vh - 64px)" }}>
        <Container
          id="intro"
          sx={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
            height: "100%",
          }}
        >
          <Typography variant="h2" fontWeight={"bold"} align="center" mt={5} mb={3}>
            Skills
          </Typography>
          <RadarChart data={skillLevels} labels={skillLabels} />
        </Container>
      </div>
    );
  };

export default Skill;
