import React, { useState, useEffect, useCallback, useRef } from 'react';
import { Box, Slide, useMediaQuery, useTheme, Swipeable } from '@mui/material';
import NavBar from './Slides/NavBar';
import Header from '../Header/Header';

const Slides = (props) => {
    const { components } = props;
    const transSpeed = 500;

    const [slideState, setSlideState] = useState({
        currentIdx: 0,
        previousIdx: 0,
        isSwitching: false,
    });

    const containerRef = useRef(null);

    useEffect(() => {
        setSlideState((prevState) => ({
            ...prevState,
            currentIdx: 0,
            previousIdx: 0,
        }));
    }, [components]);

    const updateSlideState = useCallback(
        (newIdx) => {
            setSlideState((prevState) => ({
                ...prevState,
                previousIdx: prevState.currentIdx,
                isSwitching: true,
            }));

            window.setTimeout(() => {
                setSlideState((prevState) => ({
                    ...prevState,
                    isSwitching: false,
                    currentIdx: newIdx,
                }));
            }, transSpeed);
        },
        [transSpeed]
    );

    const isSmallScreen = useMediaQuery(useTheme().breakpoints.down('sm'));

    const handleSwipe = (_, delta) => {
        if (delta < 0 && slideState.currentIdx < components.length - 1) {
            updateSlideState(slideState.currentIdx + 1);
        } else if (delta > 0 && slideState.currentIdx > 0) {
            updateSlideState(slideState.currentIdx - 1);
        }
    };

    const slideStyle = {
        width: '100%',
        height: '100%',
        position: 'fixed',
    };

    const containerStyle = {
        overflowY: 'hidden',
    };

    return (
        <Swipeable onSwiped={handleSwipe} delta={50} trackMouse>
            <Box
                ref={containerRef}
                sx={{ overscrollBehavior: 'none', ...containerStyle }}
            >
                <Header
                    orientation={isSmallScreen ? 'horizontal' : 'vertical'}
                    length={components.length}
                    currIdx={slideState.currentIdx}
                    clickHandler={updateSlideState}
                />
                <NavBar
                    orientation={isSmallScreen ? 'horizontal' : 'vertical'}
                    length={components.length}
                    currIdx={slideState.currentIdx}
                    clickHandler={updateSlideState}
                />

                <Slide direction={isSmallScreen ? 'left' : 'up'} in={!slideState.isSwitching} timeout={transSpeed}>
                    <Box sx={{ ...slideStyle, zIndex: 2 }}>
                        {components[slideState.currentIdx]}
                    </Box>
                </Slide>
            </Box>
        </Swipeable>
    );
};

export default Slides;
