import React, { useState } from "react";
import { AppBar, Toolbar, Typography, Button } from "@mui/material";
import Slides from "./Home/Slides";
import Skill from "./Skill";
import Resume from "./Resume";
import Project from "./Project";
import Contact from "./Contact";

const Header = () => {
  const [activeSection, setActiveSection] = useState("Slides");

  const handleButtonClick = (section) => {
    setActiveSection(section);
  };

  const renderSection = () => {
    switch (activeSection) {
      case "Projects":
        return <Project />;
      case "Resume":
        return <Resume />;
      case "Skills":
        return <Skill />;
      case "Contact":
        return <Contact />;
    }
  };

  return (
    <div>
      <AppBar position="fixed" color="transparent" elevation={0}>
        <Toolbar>
          <Typography variant="h5" sx={{ flexGrow: 1, textDecoration: 'none', color: 'inherit' }}>
            PORTFOLIO
          </Typography>
          <Button color="inherit" onClick={() => handleButtonClick("Projects")}>
            Projects
          </Button>
          <Button color="inherit" onClick={() => handleButtonClick("Resume")}>
            Resume
          </Button>
          <Button color="inherit" onClick={() => handleButtonClick("Skills")}>
            Skills
          </Button>
          <Button color="inherit" onClick={() => handleButtonClick("Contact")}>
            Contact
          </Button>
        </Toolbar>
      </AppBar>
      {renderSection()}
    </div>
  );
};

export default Header;
