import React, { useRef, useEffect } from "react";
import { Element, animateScroll as scroll } from "react-scroll";

const Main = ({ children }) => {
  const containerRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      // 화면의 높이
      const windowHeight = window.innerHeight;
      // container 요소
      const container = containerRef.current;

      // 현재 스크롤 위치
      const scrollPosition = window.scrollY;

      // 현재 스크롤 위치에서 어느 섹션으로 이동할지 계산
      const targetIndex = Math.floor(scrollPosition / windowHeight);

      // 해당 섹션으로 스크롤 애니메이션 적용
      scroll.scrollToTop({
        duration: 500,
        smooth: "easeInOutQuart",
        containerId: "main",
        offset: targetIndex * windowHeight,
      });
    };

    // 스크롤 이벤트 리스너 등록
    window.addEventListener("scroll", handleScroll);

    // 컴포넌트가 언마운트될 때 리스너 제거
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <main
      id="main"
      role="main"
      style={{
        display: "flex",
        flexDirection: "column",
        height: "100vh",
        overflowY: "scroll",
      }}
      ref={containerRef}
    >
      {React.Children.map(children, (child, index) => (
        <Element key={index} name={`section${index}`} className="element">
          <div
            style={{
              flex: "1",
              minHeight: "100vh",
              display: "flex",
              flexDirection: "row",
            }}
          >
            {child}
          </div>
        </Element>
      ))}
    </main>
  );
};

export default Main;
