import React, { useState } from "react";
import { AppBar, Toolbar, Typography, Button } from "@mui/material";
import { Link } from "react-router-dom"; // Import Link if you are using react-router-dom
import Intro from "./Intro";
import Skill from "./Skill";
import Resume from "./Resume";
import Project from "./Project";
import Contact from "./Contact";

const Header = () => {
    const [activeComponent, setActiveComponent] = useState("Projects");

    const handleButtonClick = (component) => {
        setActiveComponent(component);
    };

    const renderComponent = () => {
        switch (activeComponent) {
            case "Intro":
                return <Intro />;
            case "Projects":
                return <Project />;
            case "Resume":
                return <Resume />;
            case "Skills":
                return <Skill />;
            case "Contact":
                return <Contact />;
            default:
                return null;
        }
    };

    return (
        <div>
            <AppBar position="fixed" color="transparent" elevation={0}>
                <Toolbar>
                    <Typography
                        variant="h5"
                        component={Link}
                        to="/"
                        sx={{ flexGrow: 1, textDecoration: 'none', color: 'inherit' }}
                    >
                        PORTFOLIO
                    </Typography>
                    <Button color="inherit" onClick={() => handleButtonClick("Projects")}>
                        Projects
                    </Button>
                    <Button color="inherit" onClick={() => handleButtonClick("Resume")}>
                        Resume
                    </Button>
                    <Button color="inherit" onClick={() => handleButtonClick("Skills")}>
                        Skills
                    </Button>
                    <Button color="inherit" onClick={() => handleButtonClick("Contact")}>
                        Contact
                    </Button>
                </Toolbar>
            </AppBar>
            {renderComponent()}
        </div>
    );
};

export default Header;
