import React, { useState } from 'react';
import { Card, CardContent, Typography } from '@mui/material';

const SkillCard = ({ skill }) => {
  const [circlePosition, setCirclePosition] = useState({ x: 0, y: 0 });

  const handleMouseMove = (event) => {
    // 마우스 이벤트에서 현재 마우스 위치를 가져옵니다.
    const mouseX = event.nativeEvent.offsetX;
    const mouseY = event.nativeEvent.offsetY;

    // 동그라미의 위치를 업데이트합니다.
    setCirclePosition({ x: mouseX, y: mouseY });
  };

  const handleMouseLeave = () => {
    // 마우스가 이미지를 벗어날 때 동그라미의 위치를 초기화합니다.
    setCirclePosition({ x: 0, y: 0 });
  };

  return (
    <>
      <div
        style={{ position: 'relative', overflow: 'hidden' }}
        onMouseMove={handleMouseMove}
        onMouseLeave={handleMouseLeave}
      >
        {/* 이미지를 감싸는 div를 생성하여 상대적인 위치 설정 */}
        <img
          alt={skill.name}
          style={{ maxWidth: '100%', height: '10vh', objectFit: 'cover' }}
          src={skill.image}
        />
        {/* 동그라미를 나타내는 div */}
        <div
          style={{
            position: 'absolute',
            width: '20px',
            height: '20px',
            borderRadius: '50%',
            background: 'white',
            transform: `translate(${circlePosition.x - 10}px, ${circlePosition.y - 10}px)`,
            pointerEvents: 'none', // 동그라미가 마우스 이벤트를 가로채지 않도록 설정
          }}
        />
      </div>
    </>
  );
};

export default SkillCard;
