import React,{useEffect,use} from "react";
import { Container, Typography, Grid, Card, CardContent } from "@mui/material";
import { useSpring, animated } from "react-spring";
import { useGesture } from 'react-use-gesture'


const skillsData = [
  { category: "Language", skills: ["JavaScript", "Java", "C", "Python", "TypeScript", "R"] },
  { category: "Markup", skills: ["HTML5", "CSS3"] },
  { category: "Data", skills: ["MySQL", "JSON"] },
  { category: "Framework", skills: ["React", "React-Native", "FastAPI", "Node.js"] },
  { category: "Tool", skills: ["Postman", "Git", "Github", "Linux", "Jira"] },
  { category: "Design", skills: ["Figma"] },
];

const calcX = (y: number, ly: number) => -(y - ly - window.innerHeight / 2) / 20
const calcY = (x: number, lx: number) => (x - lx - window.innerWidth / 2) / 20

const wheel = (y: number) => {
  const imgHeight = window.innerWidth * 0.3 - 20
  return `translateY(${-imgHeight * (y < 0 ? 6 : 1) - (y % (imgHeight * 5))}px`
}

const Skill = () => {
    useEffect(() => {
        const preventDefault = (e: Event) => e.preventDefault()
        document.addEventListener('gesturestart', preventDefault)
        document.addEventListener('gesturechange', preventDefault)

        return () => {
          document.removeEventListener('gesturestart', preventDefault)
          document.removeEventListener('gesturechange', preventDefault)
        }
      }, [])

      const domTarget = useRef(null)
      const [{ x, y, rotateX, rotateY, rotateZ, zoom, scale }, api] = useSpring(
        () => ({
          rotateX: 0,
          rotateY: 0,
          rotateZ: 0,
          scale: 1,
          zoom: 0,
          x: 0,
          y: 0,
          config: { mass: 5, tension: 350, friction: 40 },
        })
      )

      const [{ wheelY }, wheelApi] = useSpring(() => ({ wheelY: 0 }))

      useGesture(
        {
          onDrag: ({ active, offset: [x, y] }) =>
            api({ x, y, rotateX: 0, rotateY: 0, scale: active ? 1 : 1.1 }),
          onPinch: ({ offset: [d, a] }) => api({ zoom: d / 200, rotateZ: a }),
          onMove: ({ xy: [px, py], dragging }) =>
            !dragging &&
            api({
              rotateX: calcX(py, y.get()),
              rotateY: calcY(px, x.get()),
              scale: 1.1,
            }),
          onHover: ({ hovering }) =>
            !hovering && api({ rotateX: 0, rotateY: 0, scale: 1 }),
          onWheel: ({ event, offset: [, y] }) => {
            event.preventDefault()
            wheelApi.set({ wheelY: y })
          },
        },
        { domTarget, eventOptions: { passive: false } }
      )

  return (
    <animated.div style={props}>
      <Container
        id="intro"
        sx={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          height: "100%",
        }}
      >
        <Typography variant="h4" fontWeight={"bold"} align="center" mb={5}>
          Skills
        </Typography>

        <Grid container spacing={1}>
        <animated.div style={{ transform: wheelY.to(wheel) }}>
          {skillsData.map((category, index) => (
            <Grid item key={index} xs={12} sm={6} md={4}>
              <Typography variant="h6" color={"GrayText"} mb={2} textAlign={"center"}>
                {category.category}
              </Typography>
              <Grid container spacing={1}>
                {category.skills.map((skill, skillIndex) => (
                  <Grid item key={skillIndex} xs={5} sm={4} md={6}>
                    <Card variant="outlined">
                      <CardContent>
                        <Typography variant="body1" fontWeight={"bold"} textAlign={"center"}>
                          {skill}
                        </Typography>
                      </CardContent>
                    </Card>
                  </Grid>
                ))}
              </Grid>
            </Grid>
          ))}
          </animated.div>
        </Grid>
      </Container>
    </animated.div>
  );
};

export default Skill;
