import React, { useEffect, useState } from "react";
import { Element, scroller } from "react-scroll";

const Main = ({ children }) => {
  const [currentSection, setCurrentSection] = useState(0);

  const handleScroll = () => {
    // Calculate the index of the currently visible section based on scroll position
    const scrollTop = window.scrollY;
    const sectionHeights = Array.from({ length: children.length }, (_, index) =>
      document.getElementById(`section${index}`).offsetTop
    );

    const currentSectionIndex = sectionHeights.findIndex(
      (height, index) => scrollTop >= height && (sectionHeights[index + 1] || Infinity) > scrollTop
    );

    setCurrentSection(currentSectionIndex);
  };

  useEffect(() => {
    // Attach scroll event listener
    window.addEventListener("scroll", handleScroll);

    // Cleanup event listener on component unmount
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  // Scroll to the selected section when the currentSection changes
  useEffect(() => {
    scroller.scrollTo(`section${currentSection}`, {
      duration: 800,
      delay: 0,
      smooth: "easeInOutQuart",
    });
  }, [currentSection]);

  return (
    <main>
      {React.Children.map(children, (child, index) => (
        <Element key={index} name={`section${index}`} className={`element ${index === currentSection ? 'visible' : ''}`}>
          <div
            style={{
              flex: "1",
              minHeight: "100vh",
              display: "flex",
              flexDirection: "column",
            }}
          >
            {child}
          </div>
        </Element>
      ))}
    </main>
  );
};

export default Main;
