import React, { useEffect } from "react";
import { Element, scroller } from "react-scroll";

const Main = ({ children }) => {
  const scrollToSection = (index) => {
    scroller.scrollTo(`section${index}`, {
      duration: 800,
      delay: 0,
      smooth: "easeInOutQuart",
    });
  };

  useEffect(() => {
    const handleScroll = () => {
      const currentScrollPos = window.scrollY;

      // 현재 섹션의 시작 위치를 계산
      const currentIndex = React.Children.toArray(children).findIndex(
        (_, index) => {
          const element = document.querySelector(`[name=section${index}]`);
          if (element) {
            const { top } = element.getBoundingClientRect();
            return top >= 0;
          }
          return false;
        }
      );

      if (currentScrollPos > currentIndex * window.innerHeight) {
        scrollToSection(currentIndex + 1);
      } else if (currentScrollPos < currentIndex * window.innerHeight) {
        scrollToSection(currentIndex - 1);
      }
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, [children]);

  return (
    <main>
      {React.Children.map(children, (child, index) => (
        <Element name={`section${index}`} className="element" key={index}>
          <div
            style={{
              flex: "1",
              minHeight: "100vh",
              display: "flex",
              flexDirection: "column",
            }}
          >
            {child}
          </div>
        </Element>
      ))}
    </main>
  );
};

export default Main;
