import React from "react";
import { Router, Route, Routes } from "react-router-dom";

const headerStyle = {
    position: 'fixed',
    zIndex: 10000,
  };

  const headerInnerStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    backgroundColor: 'rgba(116, 99, 99, 0.1)',
    backdropFilter: 'blur(15px)',
    padding: '1rem',
  };

  const headerLogoStyle = {
    fontSize: '0.9rem',
    textAlign: 'center',
    textTransform: 'uppercase',
    lineHeight: 1,
  };

  const headerNavStyle = {
    li: {
      display: 'inline',
    },
    a: {
      textTransform: 'uppercase',
      fontSize: '14px',
      padding: '14px',
      position: 'relative',
    },
    '&:hover::before': {
      transform: 'scaleX(1)',
    },
  };

  const headerNavMobileStyle = {
    display: 'none',
    width: '40px',
    height: '40px',
    cursor: 'pointer',
    '@media (max-width: 800px)': {
      display: 'block',
    },
    span: {
      display: 'block',
      width: '40px',
      height: '2px',
      backgroundColor: 'var(--black)',
      marginTop: '19px',
      position: 'relative',
      '&::before': {
        content: '""',
        width: '40px',
        height: '2px',
        backgroundColor: 'var(--black)',
        position: 'absolute',
        right: '0',
        top: '6px',
        transition: 'width 0.3s',
      },
      '&::after': {
        content: '""',
        width: '40px',
        height: '2px',
        backgroundColor: 'var(--black)',
        position: 'absolute',
        left: '0',
        bottom: '6px',
        transition: 'width 0.3s',
      },
    },
  };

const Header = () => {
    return (
        <Router>
          <Routes>
            <Route
              path="/"
              element={
                <div style={headerStyle}>
                  <div style={headerInnerStyle}>
                    <div style={headerLogoStyle}>
                      <h1>Welcome to My Portfolio</h1>
                      <p>Hi, I'm Heewon. I'm a passionate front-end developer.</p>
                      <Link to="/projects">View Projects</Link>
                    </div>
                    <div style={headerNavStyle}>
                      {/* Navigation items */}
                    </div>
                    <div style={headerNavMobileStyle}>
                      <span></span>
                    </div>
                  </div>
                </div>
              }
            />
            {/* Other routes */}
          </Routes>
        </Router>
      );
};

export default Header;
