import React from "react";
import { Element, animateScroll as scroll } from "react-scroll";

const Main = ({ children }) => {
  const scrollToNext = () => {
    // You can customize this function to handle scrolling to the next section
    // This is just a basic example, you might want to implement smoother scrolling
    scroll.scrollTo(window.scrollY + window.innerHeight);
  };

  return (
    <main>
      {React.Children.map(children, (child, index) => (
        <Element key={index} name={`section${index}`} className="element">
          <div
            style={{
              flex: "1",
              minHeight: "100vh",
              display: "flex",
              flexDirection: "column",
            }}
          >
            {child}
          </div>
        </Element>
      ))}
      {/* Optional: Add a button or any trigger to scroll to the next section */}
      <button onClick={scrollToNext}>Scroll to Next Section</button>
    </main>
  );
};

export default Main;
