import React, { useRef, useEffect } from "react";
import { Element, scroller } from "react-scroll";

const Main = ({ children }) => {
  const containerRef = useRef();

  const handleScroll = () => {
    const container = containerRef.current;
    const currentScrollPos = container.scrollTop;
    const sectionIndex = Math.floor(currentScrollPos / window.innerHeight);
    scroller.scrollTo(`section${sectionIndex}`, {
      duration: 800,
      delay: 0,
      smooth: "easeInOutQuart",
    });
  };

  useEffect(() => {
    const container = containerRef.current;
    container.addEventListener("scroll", handleScroll);
    return () => {
      container.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <main ref={containerRef} style={{ overflowY: "scroll" }}>
      {React.Children.map(children, (child, index) => (
        <Element key={index} name={`section${index}`} className="element">
          <div
            style={{
              flex: "1",
              minHeight: "100vh",
              display: "flex",
              flexDirection: "column",
            }}
          >
            {child}
          </div>
        </Element>
      ))}
    </main>
  );
};

export default Main;
