import React, { useRef, useEffect } from "react";
import { useSpring, animated } from "@react-spring/web";
import { Card, CardContent, Typography, useTheme, useMediaQuery, responsiveFontSizes, ThemeProvider } from "@mui/material";

const getRandomColor = () => {
  const letters = "0123456789ABCDEF";
  let color = "#";
  for (let i = 0; i < 6; i++) {
    color += letters[Math.floor(Math.random() * 16)];
  }
  return color;
};

const SkillCard = ({ skill }) => {
  const theme = useTheme();
  const isSmallScreen = useMediaQuery(theme.breakpoints.down("sm"));
  const responsiveTheme = responsiveFontSizes(theme);

  const domTarget = useRef(null);

  const [{ opacity }, api] = useSpring(() => ({
    opacity: 0,
  }));

  useEffect(() => {
    const handleScroll = () => {
      const scrollY = window.scrollY || window.pageYOffset;
      const fadeInThreshold = 300; // Adjust as needed
      const fadeOutThreshold = 1200; // Adjust as needed

      // Calculate opacity based on scroll position
      const opacityValue = Math.max(0, Math.min(1, (scrollY - fadeInThreshold) / (fadeOutThreshold - fadeInThreshold)));

      api.start({ opacity: opacityValue });
    };

    // Attach scroll event listener
    window.addEventListener("scroll", handleScroll);

    // Cleanup on component unmount
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, [api]);

  return (
    <ThemeProvider theme={responsiveTheme}>
      <animated.div
        ref={domTarget}
        style={{
          transform: "perspective(600px)",
          backgroundColor: getRandomColor(),
          opacity,
        }}
      >
        <Card variant="outlined" sx={{ width: "90%", height: "100%" }}>
          <CardContent>
            <Typography
              fontWeight={"bold"}
              sx={{
                marginLeft: { xs: "-15px", sm: "-15px", md: "px" },
                fontSize: { xs: "8px", sm: "8px", md: "16px" },
              }}
            >
              {skill}
            </Typography>
          </CardContent>
        </Card>
      </animated.div>
    </ThemeProvider>
  );
};

export default SkillCard;
