// ScrollingEffect.js
import React, { useRef, useEffect } from "react";
import { useSpring, animated } from "@react-spring/web";

const ScrollingEffect = () => {
  const domTarget = useRef(null);
  const [springProps, set] = useSpring(() => ({ opacity: 0 }));

  const handleScroll = () => {
    const options = {
      root: null,
      rootMargin: '0px',
      threshold: 0.7,
    };

    const observer = new IntersectionObserver((entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          const newOpacity = Math.min(1, Math.max(0, (window.innerHeight - entry.boundingClientRect.top) / (entry.boundingClientRect.height)));
          set({ opacity: newOpacity });
        } else {
          set({ opacity: 0 });
        }
      });
    }, options);

    if (domTarget.current) {
      observer.observe(domTarget.current);
    }
  };

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
    handleScroll();

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <animated.div
      ref={domTarget}
      style={{
        opacity: springProps.opacity,
        transition: "opacity 0.2s ease",
      }}
    />
  );
};

export default ScrollingEffect;
