import React from "react";
import { Link, Element, Events, animateScroll as scroll } from "react-scroll";

const Main = ({ children }) => {
  const scrollToTop = () => {
    scroll.scrollToTop();
  };

  return (
    <main
      id="main"
      role="main"
      style={{
        display: "flex",
        flexDirection: "column",
        height: "100vh",
        overflowY: "scroll", // Enable vertical scrolling if needed
      }}
    >
      {React.Children.map(children, (child, index) => (
        <Element key={index} name={`section${index}`} className="element">
          <div
            style={{
              flex: "1",
              minHeight: "100vh",
              display: "flex",
              flexDirection: "column",
              animationDelay: `${index * 0.5}s`, // Adjust the delay time as needed
            }}
          >
            {child}
          </div>
        </Element>
      ))}
      <Link to="section0" spy={true} smooth={true} duration={500} offset={0}>
        Scroll to Top
      </Link>
    </main>
  );
};

export default Main;
