import React, { useRef, useState, useEffect } from "react";
import { useSpring, animated } from "@react-spring/web";
import SkillCard from "./SkillCard";
import { Container, Typography, Grid, useTheme, useMediaQuery, responsiveFontSizes, ThemeProvider } from "@mui/material";

const skillsData = [
  { category: "Language", skills: ["JavaScript", "Java", "C", "Python", "TypeScript", "R"] },
  { category: "Markup", skills: ["HTML5", "CSS3"] },
  { category: "Data", skills: ["MySQL", "JSON"] },
  { category: "Framework", skills: ["React", "React-Native", "FastAPI", "Node.js"] },
  { category: "Tool", skills: ["Postman", "Git", "Github", "Linux", "Jira"] },
  { category: "Design", skills: ["Figma"] },
];

const Skill = () => {
  const theme = useTheme();
  const isSmallScreen = useMediaQuery(theme.breakpoints.down("sm"));
  const responsiveTheme = responsiveFontSizes(theme);

  const domTargets = useRef(skillsData.map(() => null));
  const [opacities, setOpacities] = useState(Array(skillsData.length).fill(0));

  const handleScroll = () => {
    const options = {
      root: null,
      rootMargin: '0px',
      threshold: 0.5, // Adjust as needed
    };

    const observer = new IntersectionObserver((entries) => {
      entries.forEach((entry, index) => {
        if (entry.isIntersecting) {
          console.log(`Element ${index + 1} is in the viewport.`);
          const newOpacity = Math.min(1, Math.max(0, (window.innerHeight - entry.boundingClientRect.top) / (entry.boundingClientRect.height)));
          setOpacities((prevOpacities) => {
            const updatedOpacities = [...prevOpacities];
            updatedOpacities[index] = newOpacity;
            return updatedOpacities;
          });
        } else {
          console.log(`Element ${index + 1} not in the viewport.`);
        }
      });
    }, options);

    domTargets.current.forEach((target) => {
      if (target) {
        observer.observe(target);
      }
    });
  };

  useEffect(() => {
    // Attach scroll event listener
    window.addEventListener("scroll", handleScroll);

    // Initial calculation
    handleScroll();

    // Cleanup on component unmount
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <ThemeProvider theme={responsiveTheme}>
      <Container
        id="intro"
        sx={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          height: "100%",
        }}
      >
        <Typography fontWeight={"bold"} variant="h2" mb={3} style={{ fontSize: isSmallScreen ? "1em" : "3em", textAlign: "center" }}>
          Skills
        </Typography>

        <Grid container spacing={1}>
          {skillsData.map((category, index) => (
            <Grid item key={index} xs={4} sm={6} md={4}>
              <Typography color={"GrayText"} mb={2} textAlign={"center"} sx={{ fontSize: { xs: '12px', sm: '14px', md: '20px' } }}>
                {category.category}
              </Typography>
              <Grid container spacing={1}>
                {category.skills.map((skill, skillIndex) => (
                  <Grid item key={skillIndex} xs={6} sm={4} md={6}>
                    <animated.div
                      ref={(ref) => (domTargets.current[index * category.skills.length + skillIndex] = ref)}
                      style={{
                        opacity: opacities[index * category.skills.length + skillIndex],
                        transition: "opacity 0.2s ease",
                      }}
                    >
                      <SkillCard skill={skill} />
                    </animated.div>
                  </Grid>
                ))}
              </Grid>
            </Grid>
          ))}
        </Grid>
      </Container>
    </ThemeProvider>
  );
};

export default Skill;
