import React from "react";
import { AppBar, Toolbar, Typography, Button } from "@mui/material";
import Slides from "./Home/Slides";
import Intro from "./Intro";
import Skill from "./Skill";
import Resume from "./Resume";
import Project from "./Project";
import Contact from "./Contact";
import NavDot from "./Home/Slides/NavDot";


const Header = ({ orientation, length, currIdx, clickHandler }) => {
    const style = useMemo(() => ({
        position: 'fixed',
        display: 'flex',
        flexDirection: orientation === 'vertical' ? 'column' : 'row',
        alignItems: 'center',
        justifyContent: 'center',
        gap: '10px',
        transform: orientation === 'vertical' ? 'translateY(-50%)' : 'translateX(-50%)',
        zIndex: 1000,
      }), [orientation]);
  return (
    <AppBar position="fixed" color="transparent" elevation={0}>
      <Toolbar>
        <Typography variant="h5" sx={{ flexGrow: 1, textDecoration: 'none', color: 'inherit' }}>
          PORTFOLIO
        </Typography>
        <Button color="inherit">
          Projects
        </Button>
        <Button color="inherit">
          Resume
        </Button>
        <Button color="inherit">
          Skills
        </Button>
        <Button color="inherit">
          Contact
        </Button>
        {Array.from(Array(length)).map((_value, idx) => (
        <NavDot
          key={idx}
          isCurrent={currIdx === idx}
          thisIdx={idx}
          clickHandler={() => clickHandler(idx)}
        />
      ))}
      </Toolbar>
    </AppBar>

  );
};

export default Header;
