import React, { useRef } from "react";
import { Element, animateScroll as scroll } from "react-scroll";

const Main = ({ children }) => {
  const mainRef = useRef(null);

  const scrollToNextSection = () => {
    const currentSectionIndex = Math.round(mainRef.current.scrollTop / window.innerHeight);
    const nextSectionIndex = currentSectionIndex + 1;
    const nextSectionName = `section${nextSectionIndex}`;
    scroll.scrollTo(ReactDOM.findDOMNode(document.getElementById(nextSectionName)), {
      smooth: true,
      duration: 500,
    });
  };

  const handleScroll = () => {
    const currentScrollTop = mainRef.current.scrollTop;
    const currentSectionIndex = Math.round(currentScrollTop / window.innerHeight);
    const isScrollingDown = currentScrollTop > lastScrollTop;

    if (isScrollingDown && currentSectionIndex !== lastSectionIndex) {
      scrollToNextSection();
    }

    setLastScrollTop(currentScrollTop);
    setLastSectionIndex(currentSectionIndex);
  };

  return (
    <main
      id="main"
      role="main"
      ref={mainRef}
      onScroll={handleScroll}
      style={{
        display: "flex",
        flexDirection: "column",
        height: "100vh",
        overflowY: "scroll", // Fix: Change 'scroll' to '"scroll"'
      }}
    >
      {React.Children.map(children, (child, index) => (
        <Element key={index} name={`section${index}`} className="element">
          <div
            style={{
              flex: "1",
              minHeight: "100vh",
              display: "flex",
              flexDirection: "row",
            }}
          >
            {child}
          </div>
        </Element>
      ))}
    </main>
  );
};

export default Main;
