import React, { useRef, useEffect } from "react";
import { Element, animateScroll as scroll, scroller } from "react-scroll";

const Main = ({ children }) => {
  const mainRef = useRef(null);

  useEffect(() => {
    // Update the scroll position when the component mounts
    updateScrollPosition();

    // Update the scroll position when the URL hash changes
    const handleHashChange = () => {
      updateScrollPosition();
    };

    window.addEventListener("hashchange", handleHashChange);

    // Remove the event listener when the component unmounts
    return () => {
      window.removeEventListener("hashchange", handleHashChange);
    };
  }, []);

  const updateScrollPosition = () => {
    const hash = window.location.hash.replace("#", "");
    const index = parseInt(hash.replace("section", ""), 10);
    if (!isNaN(index)) {
      // Scroll to the corresponding section
      scroller.scrollTo(`section${index}`, {
        duration: 800,
        delay: 0,
        smooth: "easeInOutQuart",
      });
    }
  };

  return (
    <main
      id="main"
      role="main"
      style={{
        display: "flex",
        flexDirection: "column",
        height: "100vh",
        overflowY: "scroll", // Corrected: "scroll" instead of scroll variable
      }}
      ref={mainRef}
    >
      {React.Children.map(children, (child, index) => (
        <Element key={index} name={`section${index}`} className="element">
          <div
            style={{
              flex: "1",
              minHeight: "100vh",
              display: "flex",
              flexDirection: "row",
            }}
          >
            {child}
          </div>
        </Element>
      ))}
    </main>
  );
};

export default Main;
