import React, { useRef, useState, useEffect } from "react";
import { useSpring, animated } from "@react-spring/web";
import { Card, CardContent, Typography, useTheme, useMediaQuery, responsiveFontSizes, ThemeProvider } from "@mui/material";

const getRandomColor = () => {
  const letters = "0123456789ABCDEF";
  let color = "#";
  for (let i = 0; i < 6; i++) {
    color += letters[Math.floor(Math.random() * 16)];
  }
  return color;
};

const SkillCard = ({ skill }) => {
  const theme = useTheme();
  const isSmallScreen = useMediaQuery(theme.breakpoints.down("sm"));
  const responsiveTheme = responsiveFontSizes(theme);
  const domTarget = useRef(null);

  const [scrollY, setScrollY] = useState(0);

  const handleScroll = () => {
    setScrollY(window.scrollY || window.pageYOffset);
  };

  useEffect(() => {
    // Attach scroll event listener
    window.addEventListener("scroll", handleScroll);

    // Cleanup on component unmount
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  const { opacity, backgroundColor } = useSpring({
    from: { opacity: 0, backgroundColor: getRandomColor() },
    to: async (next) => {
      // Calculate opacity based on scroll position
      const fadeInThreshold = 300; // Adjust as needed
      const fadeOutThreshold = 1200; // Adjust as needed

      let opacityValue;

      if (scrollY < fadeInThreshold) {
        // Fade in
        opacityValue = scrollY / fadeInThreshold;
      } else if (scrollY > fadeOutThreshold) {
        // Fade out
        opacityValue = 1 - Math.min((scrollY - fadeOutThreshold) / fadeInThreshold, 1);
      } else {
        // Full opacity in between
        opacityValue = 1;
      }

      await next({ opacity: opacityValue });
      await next({ backgroundColor: getRandomColor() }); // Change color after opacity transition
    },
    config: { mass: 1, tension: 180, friction: 12 },
  });

  return (
    <ThemeProvider theme={responsiveTheme}>
      <animated.div
        ref={domTarget}
        style={{
          backgroundColor,
          opacity,
          transform: "perspective(600px)",
        }}
      >
        <Card variant="outlined" sx={{ width: "90%", height: "100%" }}>
          <CardContent>
            <Typography
              fontWeight={"bold"}
              sx={{
                marginLeft: { xs: "-15px", sm: "-15px", md: "px" },
                fontSize: { xs: "8px", sm: "8px", md: "16px" },
              }}
            >
              {skill}
            </Typography>
          </CardContent>
        </Card>
      </animated.div>
    </ThemeProvider>
  );
};

export default SkillCard;
