import React, { useState, useEffect, useRef } from "react";
import { motion, useScroll, useTransform } from "framer-motion";
import {
    Container,
    Typography,
    Box,
    useTheme,
    Button,
} from "@mui/material";

const Single = React.forwardRef( ({project}, ref ) => {
    const theme = useTheme();

    const [currentImageIndex, setCurrentImageIndex] = useState(0);
    const [isHovered, setIsHovered] = useState(false);

    const handleMouseEnter = () => {
        setIsHovered(true);
    };

    const handleMouseLeave = () => {
        setIsHovered(false);
    };

    const imageUrl = project.imageUrls[0];
    // useEffect(() => {
    //     const intervalId = setInterval(() => {
    //         setCurrentImageIndex((prevIndex) => (prevIndex + 1) % project.length);
    //     }, 2000);

    //     return () => clearInterval(intervalId);
    // }, []);

    // const ref = useRef();
    const [currentProject, setCurrentProject] = useState(0);

    const handleScroll = () => {
        // Project 컴포넌트에서의 스크롤 이벤트 처리
        if (ref.current) {
            const scrollTop = ref.current.scrollTop;
            const projectHeight = ref.current.clientHeight;

            const newCurrentProject = Math.floor(scrollTop / projectHeight);

            // Update the current project only if it has changed
            if (newCurrentProject !== currentProject) {
                setCurrentProject(newCurrentProject);
            }
        }
    };

    useEffect(() => {
        // Add event listener for scroll
        ref.current.addEventListener("scroll", handleScroll);

        // Remove event listener on component unmount
        return () => {
            ref.current.removeEventListener("scroll", handleScroll);
        };
    }, [currentProject, ref]);
    console.log("ref",ref)

    const { scrollYProgress } = useScroll();
    const y = useTransform(scrollYProgress, [0, 1], [-300, 300]);

    return (
        <Container >
            <Box style={{ width: '100%',overflowY: "scroll", scrollBehavior: "smooth",}} ref={ref}>
                    <Box >
                        <img
                            alt={project.title}
                            src={imageUrl}
                            style={{
                                minHeight: "0",
                                minWidth: "0",
                                maxHeight: "50vh",
                                maxWidth: "80%",
                                border: "1px solid #65f9af",
                                borderRadius: "10px",
                                // opacity: isHovered ? 0.1 : 1,
                            }}
                        />
                        </Box>
                        <motion.div style={{ y }}>

                    {/* {isHovered && */}
                        {/* <Box
                            sx={{
                                textAlign: "center",
                                // position: "absolute",
                            }}
                        > */}
                            <Typography
                                color={'#FFF'}
                                fontWeight={"bold"}
                                textAlign={"center"}
                                mb={2}
                                sx={{ fontSize: { xs: "8px", sm: "14px", md: "20px" } }}
                            >
                                {project.title}
                            </Typography>
                            <Typography
                                color={'#FFF'}
                                fontWeight={"bold"}
                                textAlign={"center"}
                                variant="body2"
                                sx={{ fontSize: { xs: "6px", sm: "14px" } }}
                            >
                                {project.role}
                            </Typography>
                            <Typography
                                color={'#FFF'}
                                variant="body2"
                                textAlign={"center"}
                                mb={2}
                                sx={{ fontSize: { xs: "6px", sm: "14px" } }}
                            >
                                {project.description}
                            </Typography>
                            <Box sx={{ marginTop: "auto", textAlign: "center" }}>
                                {project.demoLink ? (
                                    <Button
                                        href={project.demoLink}
                                        variant="contained"
                                        target="_blank"
                                        sx={{ fontSize: { xs: "8px", sm: "14px" }, backgroundColor: '#65f9af', color: '#327C57' }}
                                    >
                                        Demo
                                    </Button>
                                ) : (
                                    <Typography
                                        variant="body2"
                                        color={'#FFF'}
                                        textAlign={"center"}
                                        mb={2}
                                        sx={{ fontSize: { xs: "6px", sm: "14px" } }}
                                    >
                                        Demo Not Available
                                    </Typography>
                                )}
                            </Box>
                        {/* </Box> */}
                        </motion.div>
                    {/* } */}
                </Box>

        </Container >
    );
});

export default Single;
