// Home.js
import React, { useEffect } from "react";
import Header from "../Header";
import Intro from "../Intro";
import Skill from "../Skill";
import Resume from "../Resume";
import Project from "../Project";
import Contact from "../Contact";
import Footer from "../Footer";
import Main from "../Main";

const Home = () => {
  useEffect(() => {
    // 스크롤 이벤트 핸들러 추가
    const handleScroll = () => {
      // 현재 스크롤 위치 확인
      const scrollY = window.scrollY;

      // 섹션 ID와 해당 섹션의 위치 정보를 담은 객체
      const sections = {
        intro: document.getElementById("intro").offsetTop,
        skill: document.getElementById("skill").offsetTop,
        project: document.getElementById("project").offsetTop,
        resume: document.getElementById("resume").offsetTop,
        contact: document.getElementById("contact").offsetTop,
      };

      // 현재 스크롤 위치에 따라 해당 섹션으로 스크롤
      for (const section in sections) {
        if (scrollY >= sections[section] && scrollY < sections[section] + window.innerHeight) {
          window.location.hash = `#${section}`;
          break;
        }
      }
    };

    // 스크롤 이벤트 리스너 등록
    window.addEventListener("scroll", handleScroll);

    // 컴포넌트 언마운트 시 이벤트 리스너 제거
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <>
      <Header />
      <Main>
        <Intro />
        <Skill />
        <Project />
        <Resume />
        <Contact />
      </Main>
      <Footer />
    </>
  );
};

export default Home;
