import React from "react";
import { Link } from "react-scroll";

const Main = ({ children }) => {
  return (
    <main
      id="main"
      role="main"
      style={{
        display: "flex",
        flexDirection: "column",
        overflowY: "scroll", // Enable vertical scrolling if needed
      }}
    >
      {React.Children.map(children, (child, index) => (
        <Link
          key={index}
          to={`section-${index}`} // Use a unique identifier for each section
          smooth
          duration={500}
          offset={-80}
        >
          <div
            id={`section-${index}`}
            style={{
              flex: "1",
              minHeight: "100vh",
              display: "flex",
              flexDirection: "column",
            }}
          >
            {child}
          </div>
        </Link>
      ))}
    </main>
  );
};

export default Main;
