import React, { useState, useEffect, useRef } from "react";
import { motion } from "framer-motion";
import {
  Container,
  Typography,
  Box,
  Button,
} from "@mui/material";

const Single = React.forwardRef(({ project }, ref) => {
  const imageUrl = project.imageUrls[0];
  const [scrollProgress, setScrollProgress] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      const scrollY = window.scrollY;
      const scrollHeight = document.documentElement.scrollHeight - window.innerHeight;
      const progress = scrollY / scrollHeight;
      setScrollProgress(progress);
    };

    // Attach scroll event listener
    window.addEventListener("scroll", handleScroll);

    // Cleanup the event listener on component unmount
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <Container>
      <Box style={{ width: "100%" }}>
        <Box ref={ref}>
          <img
            alt={project.title}
            src={imageUrl}
            style={{
              minHeight: "0",
              minWidth: "0",
              maxHeight: "50vh",
              maxWidth: "80%",
              border: "1px solid #65f9af",
              borderRadius: "10px",
            }}
          />
        </Box>

        <motion.div
          style={{
            y: -300 * scrollProgress, // Adjust the multiplier as needed
          }}
        >
          <Typography
            color={"#FFF"}
            fontWeight={"bold"}
            textAlign={"center"}
            mb={2}
            sx={{ fontSize: { xs: "8px", sm: "14px", md: "20px" } }}
          >
            {project.title}
          </Typography>
          <Typography
            color={"#FFF"}
            fontWeight={"bold"}
            textAlign={"center"}
            variant="body2"
            sx={{ fontSize: { xs: "6px", sm: "14px" } }}
          >
            {project.role}
          </Typography>
          <Typography
            color={"#FFF"}
            variant="body2"
            textAlign={"center"}
            mb={2}
            sx={{ fontSize: { xs: "6px", sm: "14px" } }}
          >
            {project.description}
          </Typography>
          <Box sx={{ marginTop: "auto", textAlign: "center" }}>
            {project.demoLink ? (
              <Button
                href={project.demoLink}
                variant="contained"
                target="_blank"
                sx={{
                  fontSize: { xs: "8px", sm: "14px" },
                  backgroundColor: "#65f9af",
                  color: "#327C57",
                }}
              >
                Demo
              </Button>
            ) : (
              <Typography
                variant="body2"
                color={"#FFF"}
                textAlign={"center"}
                mb={2}
                sx={{ fontSize: { xs: "6px", sm: "14px" } }}
              >
                Demo Not Available
              </Typography>
            )}
          </Box>
        </motion.div>
      </Box>
    </Container>
  );
});

export default Single;
