import { useEffect } from "react";
import { motion, useTransform, useMotionValue } from "framer-motion";
import { wrap } from "@motionone/utils";
import { Typography, useTheme, useMediaQuery } from "@mui/material";

function ParallaxText({ children, baseVelocity = 100, scrollPosition }) {
    const baseX = useMotionValue(0);
    const baseY = useMotionValue(0);

    const x = useTransform(baseX, (v) => `${wrap(-50, 50, v)}%`);
    const y = useTransform(baseY, (v) => `${wrap(-50, 50, v)}vh`);

    useEffect(() => {
        const updateXY = () => {
            baseX.set((scrollPosition / 1000) * baseVelocity);
            baseY.set((scrollPosition / 1000) * baseVelocity);
        };

        updateXY();

        window.addEventListener("scroll", updateXY);

        return () => {
            window.removeEventListener("scroll", updateXY);
        };
    }, [scrollPosition, baseVelocity, baseX, baseY]);

    const theme = useTheme();
    const isSmallScreen = useMediaQuery(theme.breakpoints.down("sm"));

    return (
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', overflowX: 'hidden', overflowY: 'hidden' }}>
            <motion.div style={{ x: x, y: y, width: '100%', height:'50%' }}>
                <Typography color={'#FFF'} fontWeight={"bold"} variant="h1" style={{ fontSize: isSmallScreen ? "3em" : "5.5rem", textAlign: "center" }}>
                    {children}
                </Typography>
            </motion.div>
        </div>
    );
}

export default function PreSkill({ scrollPosition }) {
    return (
        <section style={{ paddingTop: '10rem' }}>
            <ParallaxText scrollPosition={scrollPosition} baseVelocity={-10}>LET'S</ParallaxText>
            <ParallaxText scrollPosition={scrollPosition} baseVelocity={10}>START!</ParallaxText>
        </section>
    );
}
