import React, { useRef, useState, useEffect } from "react";
import { useSpring, animated } from "@react-spring/web";
import { Card, CardContent, Typography, useTheme, useMediaQuery, responsiveFontSizes, ThemeProvider } from "@mui/material";

const SkillCard = ({ skill }) => {
  const theme = useTheme();
  const responsiveTheme = responsiveFontSizes(theme);


  return (
    <ThemeProvider theme={responsiveTheme}>

        <Card variant="outlined" sx={{ width: "90%", height: "100%" }}>
          <CardContent>
            <Typography
              fontWeight={"bold"}
              sx={{
                marginLeft: { xs: "-15px", sm: "-15px", md: "px" },
                fontSize: { xs: "8px", sm: "8px", md: "16px" },
              }}
            >
              {skill}
            </Typography>
          </CardContent>
        </Card>
    </ThemeProvider>
  );
};

export default SkillCard;
