import React from "react";
import { Container, Typography, Card, CardContent, Grid, LinearProgress } from "@mui/material";

const skillsData = [
  { title: "JavaScript", level: 90 },
  { title: "React.js", level: 85 },
  { title: "HTML5", level: 90 },
  { title: "CSS3", level: 85 },
  { title: "Java", level: 90 },
  { title: "C", level: 75 },
  { title: "Python", level: 65 },
  { title: "TypeScript", level: 70 },
  { title: "HTML5", level: 95 },
  { title: "CSS", level: 100 },
  { title: "R", level: 80 },
  { title: "MySQL", level: 85 },
  { title: "React-Native", level: 85 },
  { title: "Postman", level: 80 },
  { title: "Git", level: 95 },
  { title: "Linux", level: 70 },
  { title: "Figma", level: 90 },
  { title: "FastAPI", level: 60 },
  { title: "JSON", level: 90 },
  { title: "Node.js", level: 90 },
];

const categorizeSkills = (skills) => {
  const categories = {};

  // Categorize skills
  skills.forEach((skill) => {
    const firstLetter = skill.title[0].toUpperCase();
    if (!categories[firstLetter]) {
      categories[firstLetter] = [];
    }
    categories[firstLetter].push(skill);
  });

  return categories;
};

const Skill = () => {
  const categorizedSkills = categorizeSkills(skillsData);

  return (
    <div style={{ overflowY: "auto", maxHeight: "calc(100vh - 64px)" }}>
      <Container
        id="intro"
        sx={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          height: "100%",
        }}
      >
        <Typography variant="h2" fontWeight={"bold"} align="center" mt={5} mb={3}>
          Skills
        </Typography>
        {Object.entries(categorizedSkills).map(([category, skills]) => (
          <div key={category}>
            <Typography variant="h4" align="center" mt={3}>
              {category}
            </Typography>
            <Grid container spacing={1}>
              {skills.map((skill, index) => (
                <Grid item key={index} xs={12} sm={6} md={4}>
                  <Card elevation={3}>
                    <CardContent>
                      <Typography variant="h6" color="textSecondary" gutterBottom>
                        {skill.title}
                      </Typography>
                      <LinearProgress variant="determinate" value={skill.level} />
                      <Typography variant="body2" color="textSecondary" mt={2}>
                        Level: {skill.level}%
                      </Typography>
                    </CardContent>
                  </Card>
                </Grid>
              ))}
            </Grid>
          </div>
        ))}
      </Container>
    </div>
  );
};

export default Skill;
