import React, { useRef, useEffect } from "react";
import { Element, animateScroll as scroll, scroller } from "react-scroll";

const Main = ({ children }) => {
  const mainRef = useRef();

  useEffect(() => {
    const handleScroll = () => {
      // Calculate the index of the visible child
      const scrollTop = mainRef.current.scrollTop;
      const childIndex = Math.floor(scrollTop / window.innerHeight);

      // Scroll to the corresponding element using react-scroll's scroller
      scroller.scrollTo(`section${childIndex}`, {
        duration: 800,
        delay: 0,
        smooth: "easeInOutQuart",
      });
    };

    // Attach the scroll event listener to the main container
    mainRef.current.addEventListener(scroll, handleScroll);

    // Clean up the event listener on component unmount
    return () => {
      mainRef.current.removeEventListener(scroll, handleScroll);
    };
  }, []);

  return (
    <main
      id="main"
      role="main"
      style={{
        display: "flex",
        flexDirection: "column",
        height: "100vh",
        overflowY: "scroll",
      }}
      ref={mainRef}
    >
      {React.Children.map(children, (child, index) => (
        <Element key={index} name={`section${index}`} className="element">
          <div
            style={{
              flex: "1",
              minHeight: "100vh",
              display: "flex",
              flexDirection: "row",
            }}
          >
            {child}
          </div>
        </Element>
      ))}
    </main>
  );
};

export default Main;
