// Project.jsx
/*
SPDX-FileCopyrightText: © 2024 Heewon Kim <khw0285@gmail.com>
SPDX-License-Identifier: MIT
*/

import React, { useRef, useState, useEffect } from 'react';
import {
    Container,
    Typography,
    Box,
    useTheme,
    useMediaQuery,
    responsiveFontSizes,
    ThemeProvider,
    Button,
} from "@mui/material";
import Carousel from "react-material-ui-carousel";

const Project = () => {
  const [currentProjectIndex, setCurrentProjectIndex] = useState(0);
  const projectRef = useRef();

  const handleNextProject = () => {
    setCurrentProjectIndex((prevIndex) => (prevIndex + 1) % projectsData.length);
  };

  useEffect(() => {
    // Scroll to the next project when the current one changes
    projectRef.current.scrollIntoView({ behavior: 'smooth' });
  }, [currentProjectIndex]);

  return (
    <div>
      <h1>Project Showcase</h1>
      {projectsData.map((project, index) => (
        <Box key={project.id} ref={index === currentProjectIndex ? projectRef : null}>
          <h2>{project.title}</h2>
          <p>{project.role}</p>
          <p>{project.description}</p>
          <img src={project.imageUrls[0]} alt={`Screenshot ${index + 1}`} style={{ maxWidth: '100%' }} />
          {project.demoLink && <a href={project.demoLink}>Demo Link</a>}
        </Box>
      ))}
      <button onClick={handleNextProject}>Next Project</button>
    </div>
  );
};

export default Project;
